using System;
using System.Collections.Generic;
using System.Linq;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using OeeCalculation.TrackableDatabase;

namespace OeeCalculation.Test.TrackableDatabase
{
    [TestClass]
    public class DatabaseChangeSetTest
    {
        [TestMethod]
        public void TestMethod1()
        {
            byte[] data =
                StringToByteArray(
                    "");
            var t = new DatabaseChangeSet(data);
            
            t.Load();

        }
        public static byte[] StringToByteArray(String hex)
        {
            var byteList = new List<byte>();
            for (int i = 0; i < hex.Length / 2; i++)
            {
                string hexNumber = hex.Substring(i * 2, 2);
                byteList.Add((byte)Convert.ToInt32(hexNumber, 16));
            }

            byte[] original = byteList.ToArray();
            return original;
        }
    }
}
